// Erstelle ein zufälliges RGB-Farbschema mit höherer Wahrscheinlichkeit für klare Farben und begrenzten Werten
function generateRandomRGB() {
    const probability = Math.random();
    let r, g, b;
    if (probability < 0.25) { // Wahrscheinlichkeit von 25% für klares Rot
        r = 255;
        g = Math.floor(Math.random() * 96); // G-Wert begrenzt, um Weiß zu vermeiden
        b = Math.floor(Math.random() * 96); // B-Wert begrenzt, um Weiß zu vermeiden
    } else if (probability < 0.5) { // Wahrscheinlichkeit von 25% für klares Grün
        r = Math.floor(Math.random() * 96); // R-Wert begrenzt, um Weiß zu vermeiden
        g = 255;
        b = Math.floor(Math.random() * 96); // B-Wert begrenzt, um Weiß zu vermeiden
    } else if (probability < 0.75) { // Wahrscheinlichkeit von 25% für klares Blau
        r = Math.floor(Math.random() * 96); // R-Wert begrenzt, um Weiß zu vermeiden
        g = Math.floor(Math.random() * 96); // G-Wert begrenzt, um Weiß zu vermeiden
        b = 255;
    } else { // Wahrscheinlichkeit von 25% für Pink (Mischung aus Rot und Blau)
        r = 255;
        g = Math.floor(Math.random() * 96); // G-Wert begrenzt, um Weiß zu vermeiden
        b = 255;
    }
    return [r, g, b];
}

