function setHeatingTemperature(temperature) {
    // Konvertieren Sie die Temperatur in das IEEE 754 Float Little Endian Format
    let tempBuffer = convertTemperatureToFloatLE(temperature);

    // Erstellen des Befehls ohne CRC
    let command = Buffer.from([
        0x10, 0x02, 0x01, 0x13, 0x00, 0xf8
    ]);

    // Füge den Float-Wert der Temperatur hinzu
    command = Buffer.concat([command, tempBuffer]);

    // Füge den Abschluss des Befehls hinzu (0x10 und 0x03)
    command = Buffer.concat([command, Buffer.from([0x10, 0x03])]);

    // Berechnen der CRC-16/BUYPASS-Prüfsumme für Byte 3 bis 10
    let crc = crc16Bypass(command.slice(2, 10));
    let crcBuffer = Buffer.alloc(2);
    crcBuffer.writeUInt16BE(crc, 0); // Write CRC as big-endian

    // Füge CRC hinzu
    command = Buffer.concat([command, crcBuffer]);

    return command;
}



function convertTemperatureToFloatLE(temperature) {
    // Konvertieren Sie die Temperatur in Float und dann in ein Buffer-Objekt
    let buffer = Buffer.alloc(4);
    buffer.writeFloatLE(temperature, 0);
    return buffer;
}

function crc16Bypass(data) {
    var crc = 0x0000;
    var polynomial = 0x8005;

    for (var i = 0; i < data.length; i++) {
        crc ^= (data[i] << 8);

        for (var j = 0; j < 8; j++) {
            if ((crc & 0x8000) !== 0) {
                crc = (crc << 1) ^ polynomial;
            } else {
                crc <<= 1;
            }
        }
    }

    return crc & 0xFFFF;
}


