// Funktion zum Ersetzen von zwei aufeinanderfolgenden Hex 10-Werten durch eine einzelne Hex 10
function replaceConsecutiveHex10(buffer) {
    let outputBuffer = Buffer.alloc(0);
    for (let i = 0; i < buffer.length; i++) {
        if (buffer[i] === 0x10 && buffer[i + 1] === 0x10) {
            outputBuffer = Buffer.concat([outputBuffer, Buffer.from([0x10])]);
            i++; // Überspringe das nächste Hex 10
        } else {
            outputBuffer = Buffer.concat([outputBuffer, Buffer.from([buffer[i]])]);
        }
    }
    return outputBuffer;
}

// Anwenden der Funktion auf den Eingangs-Buffer und Senden des Ergebnisses
let outputBuffer = replaceConsecutiveHex10(msg.payload);
msg.payload = outputBuffer;
return msg;
